mapper.update:
UPDATE sf_character
SET
soft_res_count = (
SELECT COUNT(*) FROM sf_character_resource WHERE is_deleted = 0 AND character_id = #{id} AND `subject` = 71
),
hard_res_count = (
SELECT COUNT(*) FROM sf_character_resource WHERE is_deleted = 0 AND character_id = #{id} AND `subject` = 72
)
WHERE id = #{id}

mapper.select:
select * from sf_character where is_deleted = 0
AND char_s IN

    #{char}

mapper.update:
UPDATE sf_character
SET soft_res_visited_count = (
    SELECT t.num FROM (
        SELECT IFNULL( soft_res_visited_count, 0 ) + 1 AS num FROM sf_character WHERE id = #{id}
        ) t
)
WHERE id = #{id}

UPDATE sf_character
SET hard_res_visited_count = (
    SELECT t.num FROM (
        SELECT IFNULL( hard_res_visited_count, 0 ) + 1 AS num FROM sf_character WHERE id = #{id}
        ) t
)
WHERE id = #{id}

mapper.select:
SELECT c.id, IF(tlc.`subject` = 71, tl.er_code, null) soft_er_code, IF(tlc.`subject` = 71, tl.`code`, null) soft_code, IF(tlc.`subject` = 72, tl.er_code, null) hard_er_code, IF(tlc.`subject` = 72, tl.`code`, null) hard_code FROM sf_character c LEFT JOIN sf_textbook_lesson_character tlc ON tlc.character_id = c.id LEFT JOIN sf_textbook_lesson tl ON tlc.lesson_id = tl.id LEFT JOIN sf_textbook t ON tl.textbook_id = t.id WHERE 1=1 AND c.is_deleted = 0 AND tlc.is_deleted = 0 AND tl.is_deleted = 0 AND t.is_deleted = 0
AND t.id = #{param1}
GROUP BY c.id
