mapper.select:
SELECT * FROM sf_user_lesson
WHERE is_deleted = 0
AND lesson_id = #{lessonId}
AND user_id = #{userId}

mapper.update:
UPDATE sf_user_lesson
SET

    char_count = #{charCount},


    finished_char_count = #{finishedCharCount},


    locked = #{locked},


    starCount = #{starCount},

user_id = #{userId},
lesson_id = #{lessonId}
WHERE user_id = #{userId}
AND lesson_id = #{lessonId}
